<?php

function saveAll($red, $config, $unique)
{
	// Directorio de trabajo
	$directorio = "/var/www/html/uploads/" . $unique;
	
	// red.csv
	$filename = $directorio . "/red.csv";
	$file = fopen($filename, "w");
	fwrite($file, $red);
	fclose($file);	
	
	// config
	$filename = $directorio . "/config.ini";
	$file = fopen($filename, "w");
	fwrite($file, $config);
	fclose($file);	

	$response = array
	(
		'success' => true,
	    'msg' => 'Successfully saved'
	);
	echo json_encode($response);
}

